package bean;

public class Caybean {
	private String macay;
	private String tencay;
	private long gia;
	private long soluong;
	private String anh;
	private String maloai;
	public Caybean() {
		super();
		// TODO Auto-generated constructor stub
	}
	public Caybean(String macay, String tencay, long gia, long soluong, String anh, String maloai) {
		super();
		this.macay = macay;
		this.tencay = tencay;
		this.gia = gia;
		this.soluong = soluong;
		this.anh = anh;
		this.maloai = maloai;
	}
	public String getMacay() {
		return macay;
	}
	public void setMacay(String macay) {
		this.macay = macay;
	}
	public String getTencay() {
		return tencay;
	}
	public void setTencay(String tencay) {
		this.tencay = tencay;
	}
	public long getGia() {
		return gia;
	}
	public void setGia(long gia) {
		this.gia = gia;
	}
	public long getSoluong() {
		return soluong;
	}
	public void setSoluong(long soluong) {
		this.soluong = soluong;
	}
	public String getAnh() {
		return anh;
	}
	public void setAnh(String anh) {
		this.anh = anh;
	}
	public String getMaloai() {
		return maloai;
	}
	public void setMaloai(String maloai) {
		this.maloai = maloai;
	}
	
	
	
}
